import React from "react";
import { useSelector } from "react-redux";

function Intro() {
  const { portfolioData } = useSelector((state) => state.root);
  const {intro} = portfolioData;
  const {firstName, lastName, welcomeText, description, caption} = intro;
  return (
    <div className="h-[90vh] bg-primary flex flex-col items-start justify-center gap-8 py-10">
      <h1 className="text-black text-xl">{welcomeText || ''}</h1>
      <h1 className="text-7xl sm:text-3xl text-secondary font-semibold">{firstName || ''} {lastName || ''}</h1>
      <h1 className="text-5xl sm:text-3xl text-black font-semibold">
        {caption || ''}
      </h1>
      <p className="text-black w-2/3 text-xl">
        {description || ''}
      </p>
      <button className="border-2 border-tertiary text-tertiary px-10 py-3 rounded">Get Started</button>
    </div>
  );
}

export default Intro;
