import React, { useEffect } from "react";
import Header from "../../components/Header";
import { Tabs } from "antd";
import AdminIntro from "./AdminIntro";
import AdminAbout from "./AdminAbout";
import { useSelector } from "react-redux";
import Experiences from "./AdminExperiences";
import AdminCourses from "./AdminCourses";
import AdminContact from "./AdminContact";

const { TabPane } = Tabs;

function Admin() {
  const { portfolioData } = useSelector((state) => state.root);

  useEffect(() => {

    if(!localStorage.getItem("token")){
      window.location.href="/admin-login";
    }
  }, []);

  return (
    <div>
      <Header />
      <div className='flex gap-10 items-center px-5 py-2 justify-between'>
      <div className="flex gap-10 items-center">
      <h1 className='text-3xl text-black'>Admin Panel </h1>
            <div className='w-60 h-[1px] bg-gray-500' ></div> 
      </div>
      <h1 className="underline text-primary text-xl cursor-pointer" 
      onClick={() => {
        localStorage.removeItem("token");
        window.location.href="/admin-login";
      }}
      >Logout</h1>
    </div>

      {portfolioData && (
        <div className="px-5">
          <Tabs defaultActiveKey="1">
            <TabPane tab="Intro" key="1">
              <AdminIntro />
            </TabPane>
            <TabPane tab="About" key="2">
              <AdminAbout />
            </TabPane>
            <TabPane tab="Experiences" key="3">
              <Experiences />
            </TabPane>
            <TabPane tab="Courses" key="4">
              <AdminCourses />
            </TabPane>
            <TabPane tab="Contact" key="5">
              <AdminContact />
            </TabPane>
          </Tabs>
        </div>
      )}
    </div>
  );
}

export default Admin;
